    public static int countCharacters(String[] words, String chars) {
        int result = 0;

        int cl = chars.length();

        List<Character> characterList = new ArrayList<>();
        for (int i = 0; i < cl; i++) {
            characterList.add(chars.charAt(i));
        }

        for (String word : words) {
            if (word.length() > chars.length()) {
                continue;
            }
            result += canFormWord(word, new ArrayList<>(characterList));
        }

        return result;
    }

    private static int canFormWord(String word, List<Character> characterList) {
        for (int i = 0; i < word.length(); i++) {
            char ca = word.charAt(i);
            int index = characterList.indexOf(ca);
            if (!characterList.contains(ca)) {
                return 0;
            }
            characterList.remove(index);
        }

        return word.length();
    }


